import React, { useState } from 'react';
import { List, ListItem, ListItemText, Divider, TextField, Button, Avatar } from '@mui/material';

const Messages = () => {
  const [conversations, setConversations] = useState([
    { id: 1, name: 'Instructor', lastMessage: 'About your assignment...', unread: true },
    { id: 2, name: 'Classmate 1', lastMessage: 'Can you help me with...', unread: false },
    { id: 3, name: 'Classmate 2', lastMessage: 'Thanks for the notes!', unread: false },
  ]);
  const [activeConversation, setActiveConversation] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([
    { id: 1, sender: 'Instructor', text: 'Hi there! About your assignment...', time: '10:30 AM' },
    { id: 2, sender: 'You', text: 'Yes, I have a question', time: '10:32 AM' },
  ]);

  const handleSendMessage = () => {
    if (message.trim() && activeConversation) {
      const newMessage = {
        id: Date.now(),
        sender: 'You',
        text: message,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      };
      setMessages([...messages, newMessage]);
      setMessage('');
      
      // Update last message in conversations
      setConversations(conversations.map(conv => {
        if (conv.id === activeConversation.id) {
          return { ...conv, lastMessage: message, unread: false };
        }
        return conv;
      }));
    }
  };

  return (
    <div style={{ display: 'flex', height: 'calc(100vh - 150px)' }}>
      <div style={{ width: '300px', borderRight: '1px solid #eee', overflowY: 'auto' }}>
        <List>
          {conversations.map(conv => (
            <ListItem 
              button 
              key={conv.id}
              selected={activeConversation?.id === conv.id}
              onClick={() => {
                setActiveConversation(conv);
                // Mark as read when selected
                setConversations(conversations.map(c => 
                  c.id === conv.id ? { ...c, unread: false } : c
                ));
              }}
            >
              <Avatar sx={{ mr: 2 }}>{conv.name.charAt(0)}</Avatar>
              <ListItemText 
                primary={conv.name} 
                secondary={conv.lastMessage} 
                primaryTypographyProps={{ 
                  fontWeight: conv.unread ? 'bold' : 'normal' 
                }} 
                secondaryTypographyProps={{ 
                  fontWeight: conv.unread ? 'bold' : 'normal',
                  color: conv.unread ? 'text.primary' : 'text.secondary'
                }}
              />
            </ListItem>
          ))}
        </List>
      </div>
      
      {activeConversation ? (
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
          <div style={{ padding: '16px', borderBottom: '1px solid #eee' }}>
            <Typography variant="h6">{activeConversation.name}</Typography>
          </div>
          
          <div style={{ flex: 1, overflowY: 'auto', padding: '16px' }}>
            {messages.map(msg => (
              <div 
                key={msg.id} 
                style={{ 
                  marginBottom: '8px',
                  textAlign: msg.sender === 'You' ? 'right' : 'left'
                }}
              >
                <div style={{ 
                  display: 'inline-block',
                  padding: '8px 12px',
                  borderRadius: '8px',
                  background: msg.sender === 'You' ? '#1976d2' : '#eee',
                  color: msg.sender === 'You' ? 'white' : 'black',
                  maxWidth: '70%'
                }}>
                  <div>{msg.text}</div>
                  <div style={{ 
                    fontSize: '0.75rem', 
                    textAlign: 'right',
                    color: msg.sender === 'You' ? 'rgba(255,255,255,0.7)' : 'rgba(0,0,0,0.5)'
                  }}>
                    {msg.time}
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          <div style={{ padding: '16px', borderTop: '1px solid #eee' }}>
            <div style={{ display: 'flex' }}>
              <TextField
                fullWidth
                variant="outlined"
                size="small"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type a message..."
              />
              <Button 
                variant="contained" 
                onClick={handleSendMessage}
                sx={{ ml: 1 }}
              >
                Send
              </Button>
            </div>
          </div>
        </div>
      ) : (
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Typography variant="h6" color="text.secondary">
            Select a conversation to start messaging
          </Typography>
        </div>
      )}
    </div>
  );
};

export default Messages;
